mapper.insert:
/*member - memberInsert */
select customer_seq.nextval from dual
insert into customer(c_num, c_id, c_pwd,c_name, c_birth,c_email,c_phone,c_address, rk_no) values(#{c_num},#{c_id},#{ c_pwd},#{c_name},#{c_birth},#{c_email},#{c_phone},#{c_address},'1')

mapper.select:
select c_num, c_name, c_id, c_pwd, substr(c_birth,1,6) as c_birth,
c_email, c_phone, c_address from customer
where c_id = #{c_id}

mapper.update:
update customer set

	c_pwd=#{c_pwd},

c_phone=#{c_phone},
c_address=#{c_address},
c_email=#{c_email}
where c_id=#{c_id}
