mapper.insert:
/*member - memberInsert */
select customer_seq.nextval from dual
insert into customer(c_num, c_id, c_pwd,c_name, c_birth,c_email,c_phone,c_address, rk_no) values(#{c_num},#{c_id},#{ c_pwd},#{c_name},#{c_birth},#{c_email},#{c_phone},#{c_address},'1')

mapper.select:
select rk_rank 
from rank inner join customer on rank.rk_no = customer.rk_no
where c_num = #{c_num}
