mapper.insert:
/*Order - orderInsert*/
select orders_seq.nextval from dual
insert into orders(o_no, o_delivery, o_comment, b_no, o_date, o_deliveryaddress, o_deliveryname, o_deliveryphone) values(#{o_no},'결제완료', #{o_comment}, #{b_no}, sysdate, #{o_deliveryaddress}, #{o_deliveryname}, #{o_deliveryphone})
