mapper.insert:
	/* Qna - qnaInsert */

	
	insert into qna(q_no, q_title, q_content, reproot, repStep, repindent, c_num
	,q_img )
	values(qna_seq.nextval, #{q_title}, #{q_content}, qna_seq.currval, 0, 0 ,#{c_num}
	,#{q_img} )

mapper.select:
/* Qna - qnaDetail */
select q_no, q_title, q_content, c_id, to_char(q_date, 'YYYY-MM-DD HH24:MI:SS') as q_date, q_img,q_hit,reproot, repStep, repindent
from qna q inner join customer c on q.c_num = c.c_num where q_no=#{q_no}

mapper.update:

/* Qna - qnaUpdate */
UPDATE qna set q_title = #{q_title}, q_content = #{q_content}, q_date = sysdate		

,c_pwd = #{c_pwd}


,q_img = #{q_img}

WHERE q_no = #{q_no}

mapper.select:
/* Qna - pwdConfirm */
SELECT NVL((
			SELECT 1 FROM qna 
			WHERE q_no = #{q_no} AND c_pwd = #{c_pwd}
			), 0) as state 
			FROM qna q inner join customer c on q.c_num = c.c_num where q_no=#{q_no}

mapper.update:
UPDATE qna
     SET q_hit = NVL(q_hit, 0) + 1 
WHERE q_no=#{q_no}
